import React, { Component } from 'react'

import './app-style.css'

import Header from './components/header/component'
import NumberGenerator from './components/num-generator/component'
import Footer from './components/footer/component'

class App extends Component {
  constructor() {
    super()
    this.state = {
      numberGenerators: [
        {
          name: 'num gen wonski',
          children: 15,
          minNumberRange: 7,
          maxNumberRange: 77
        },
        {
          name: 'num gen twoski',
          children: 15,
          minNumberRange: 7,
          maxNumberRange: 77
        },
        {
          name: 'num gen threeski',
          children: 15,
          minNumberRange: 7,
          maxNumberRange: 77
        },
        {
          name: 'num gen fourski',
          children: 15,
          minNumberRange: 7,
          maxNumberRange: 77
        },
        {
          name: 'num gen fiveski',
          children: 15,
          minNumberRange: 7,
          maxNumberRange: 77
        },
        {
          name: 'num gen anotherOne',
          children: 15,
          minNumberRange: 7,
          maxNumberRange: 77
        },
        {
          name: 'num gen anotherOne',
          children: 15,
          minNumberRange: 7,
          maxNumberRange: 77
        },
        {
          name: 'num gen anotherOne',
          children: 15,
          minNumberRange: 7,
          maxNumberRange: 77
        }
      ]
    }
  }
  render() {

    // map through values here to keep JSX in the render return less cluttered
    let numGens = this.state.numberGenerators
    .map( (item, index) => {
      return <NumberGenerator name={item.name} key={index}/>
    })

    /*
    Throughout the app component structure, global classes may be used that reference
    the app-style.css file, these classes are identified by the -gl suffix,
    e.g. flex-container-gl
    */
    return (
      <div className="app-wrapper border-box-gl flex-container-gl">
        <Header/>
        <section className="app-middle border-box-gl flex-container-gl">
          {numGens}
        </section>
        <Footer/>
      </div>
    );
  }
}

export default App;
