import React, { Component } from 'react'
import './app-style.css'

// Trying to figure out best way to apply universal reset to box-sizing
import { boxSize } from './constants'

import Header from './components/header/component'
import NumberGenerator from './components/num-generator/component'

class App extends Component {
  constructor() {
    super()
    this.state = {
      numberGenerators: [
        {name: 'num gen wonski'},
        {name: 'num gen twoski'},
        {name: 'num gen threeski'},
        {name: 'num gen fourski'},
        {name: 'num gen fiveski'},
        {name: 'num gen anotherOne'},
        {name: 'num gen anotherOne'},
        {name: 'num gen anotherOne'}
      ]
    }
  }
  render() {

    // map through values here to keep JSX in the render return less cluttered
    let numGens = this.state.numberGenerators
    .map( (item, index) => {
      return <NumberGenerator name={item.name} key={index}/>
    })

    return (
      <div className="app-wrapper border-box">
        <Header/>
        <div className="app-middle border-box flex-container">
          {numGens}
        </div>
      </div>
    );
  }
}

export default App;
