      // This returns a random number between min - max
      // and includes both min & max as possible values
      return Math.random() * ( (max + 1) - min) + min;
    }

    function generateChildComponents(arg) {
      for(let i = 0; i < arg; i++) {
        // return <NumberChild value={childComponentNumber(this.props.min, this.props.max)}/>
      }
    }

    return (
      <div className="numgen-wrapper border-box-gl flex-container-gl">
        {this.props.name}
