import React, { Component } from 'react'

import './app-style.css'

import Header from './components/header/component'
import NumberGenerator from './components/num-generator/component'
import Footer from './components/footer/component'
import array from './fakeData'

class App extends Component {
  constructor() {
    super()
    this.state = {
      numberGenerators: array,
      showModal: false,
    }
  }

  closeModal() {
    this.setState({
      showModal: false,
    })
  }

  openModal() {
    this.setState({
      showModal: true,
    })
  }

  modalDisplay() {
    if (this.state.showModal) {
      return (
        <form>
          <div onClick={this.closeModal}>x</div>
          stuff and things!
        </form>)
    }
  }

  render() {


    // map through values here to keep JSX in the render return less cluttered
    let numGens = this.state.numberGenerators
    .map( (item, index) => {
      return <NumberGenerator
      name={item.name}
      howMany={item.children}
      min={item.minNumberRange}
      max={item.maxNumberRange}
      key={index}/>
    })

    // Throughout the app component tree, global classes may be used that reference
    // the app-style.css file, these classes are identified by the -gl suffix,
    // e.g. flex-container-gl
    return (
      <div className="app-wrapper border-box-gl flex-container-gl">
        <Header/>
        <section className="app-middle border-box-gl flex-container-gl">
          <div onClick={this.openModal} className="add-one border-box-gl flex-container-gl">+</div>
          {numGens}
        </section>
        <Footer/>
      </div>
    );
  }
}

export default App;
